  color: white;
}

sp_widget.script:
(function() {
  data.vehicles = [];
  var gr = new GlideRecord('x_759169_lva_loaner_vehicle_catalog');
  gr.addQuery('vehicle_model', 'Tacoma Access Cab');
  gr.query();

  while(gr.next()) {
    var vehicle = {};
    vehicle.number = gr.getValue('number');
    vehicle.status = gr.getValue('status');
    vehicle.description = gr.getValue('vehicle_description');
    vehicle.make = gr.getValue('vehicle_make');
    vehicle.model = gr.getValue('vehicle_model');
    vehicle.year = gr.getValue('vehicle_year');
    data.vehicles.push(vehicle);
  }
})();
