sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.css:
#loaner-vehicle-table {
  width: 100%;
  border-collapse: collapse;
}

#loaner-vehicle-table th, #loaner-vehicle-table td {
  border: 1px solid #ddd;
  padding: 8px;
}

#loaner-vehicle-table tr:nth-child(even) {
  background-color: #f2f2f2;
}

