  padding-top: 12px;
  padding-bottom: 12px;
  text-align: left;
  background-color: #27A2AA;
  color: white;
}

sp_widget.script:
(function() {
  data.vehicles = [];
  var gr = new GlideRecord('x_759169_lva_loaner_vehicle_catalog');
  gr.addQuery('vehicle_model', 'Colorado Crew Cab');
  gr.query();
  while(gr.next()) {
    data.vehicles.push({
      number: gr.number.getDisplayValue(),
      status: gr.status.getDisplayValue(),
      description: gr.vehicle_description.getDisplayValue(),
      make: gr.vehicle_make.getDisplayValue(),
      model: gr.vehicle_model.getDisplayValue(),
      year: gr.vehicle_year.getDisplayValue()
    });
  }
})();
