sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Only proceed if the Vehicle Status on the current Vehicle Tracker record is set to Sent for Servicing
    if (current.vehicle_status != 'sent for servicing') {
        return;
    }

    // Create a new GlideRecord object for the Vehicle Servicing table
    var gr = new GlideRecord('x_759169_lva_vehicle_servicing');

    // Add query to check if there is an existing Vehicle Servicing ticket associated to the current Vehicle Tracker that is open
    gr.addQuery('vehicle_tracker', current.sys_id);
    gr.addQuery('u_ticket_status', 'open');
    gr.query();

    // If there is no open servicing ticket, then create a new one
    if (!gr.next()) {

        // Create a new GlideRecord object for the Vehicle Servicing table
        var newServicingTicket = new GlideRecord('x_759169_lva_vehicle_servicing');

        // Set the values for the new Servicing ticket
        newServicingTicket.vehicle_tracker = current.sys_id;  // Vehicle Tracker from which the Servicing ticket was opened
        newServicingTicket.u_vehicle = current.vehicle;  // Vehicle set on the Vehicle Tracker
        newServicingTicket.u_vehicle_status = 'in service';  // Vehicle status set to in service
        newServicingTicket.u_date_sent_for_servicing = new GlideDateTime();  // The Date from when the servicing ticket was created
        newServicingTicket.u_ticket_status = 'open';  // Ticket Status set to Open


        // Insert the new servicing ticket into the database
        newServicingTicket.insert();
    }

})(current, previous);
