sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    if (current.u_ticket_status != 'closed') {
        return;
    }

    // Create a new GlideRecord object for the Vehicle Tracker table
    var gr = new GlideRecord('x_759169_lva_vehicle_tracker');

    // Retrieve the Vehicle Tracker record associated with the current Vehicle Servicing ticket
    if (gr.get(current.vehicle_tracker)) {

        // Check the u_vehicle_status on the Vehicle Servicing ticket and map it to the corresponding vehicle_status on the Vehicle Tracker
        switch (current.u_vehicle_status) {
            case 'in service':
                gr.vehicle_status = 'sent for servicing';
                break;
            case 'returned to warehouse':
                gr.vehicle_status = 'returned to warehouse';
                break;
            case 'decommissioned':
                gr.vehicle_status = 'decommissioned';
                break;
        }

        // Set the Tracker Status of the Vehicle Tracker to Closed
        gr.tracker_status = 'closed';

        // Update the Vehicle Tracker record in the database
        gr.update();
    }
})(current, previous);
