var getUserInfo = Class.create();
getUserInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

getLocation: function(){


	var user_id = this.getParameter('sysparm_user_sys_id');
	var result = ''; 

	var gr = new GlideRecord('sys_user');
	gr.addQuery('sys_id', user_id);
	gr.query();

	if(gr.next()){
		result = gr.getValue('location');
	}

	return result;



},

getDepartment: function () {

},

getContact: function () { 

},

    type: 'getUserInfo'
});
